package org.apache.kyuubi.engine.chat.ernie.api;

import com.fasterxml.jackson.annotation.JsonProperty;

import java.util.Objects;

public class ApiError {
    @JsonProperty("error_msg")
    String errorMsg;
    @JsonProperty("error_code")
    int errorCode;

    public ApiError() {
    }

    public ApiError(String errorMsg, int errorCode) {
        this.errorMsg = errorMsg;
        this.errorCode = errorCode;
    }

    public String getErrorMsg() {
        return errorMsg;
    }

    public void setErrorMsg(String errorMsg) {
        this.errorMsg = errorMsg;
    }

    public int getErrorCode() {
        return errorCode;
    }

    public void setErrorCode(int errorCode) {
        this.errorCode = errorCode;
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        ApiError apiError = (ApiError) o;
        return errorCode == apiError.errorCode && Objects.equals(errorMsg, apiError.errorMsg);
    }

    @Override
    public int hashCode() {
        return Objects.hash(errorMsg, errorCode);
    }
}
